/* Styled Card Container */
.form-card {
  max-width: 420px;
  background: #ffffff;
  margin: 40px auto;
  padding: 28px 32px 22px 32px;
  border-radius: 12px;
  box-shadow: 0 6px 28px rgba(35,72,135,0.12);
  border: 1px solid #e8eaf6;
}

.form-card h2 {
  margin-bottom: 20px;
  color: #234887;
  font-weight: 600;
  text-align: center;
}

label {
  display: block;
  font-weight: 500;
  margin-top: 16px;
  margin-bottom: 6px;
  color: #2b4257;
  font-size: 1rem;
}

input[type="number"] {
  width: 100%;
  padding: 9px 12px;
  border-radius: 6px;
  border: 1px solid #b0bec5;
  font-size: 1rem;
  background: #f5f7fa;
  margin-bottom: 8px;
  transition: border-color 0.2s;
}

input[type="number"]:focus {
  outline: none;
  border-color: #234887;
}

input[type="submit"] {
  width: 100%;
  padding: 12px 0;
  background: #234887;
  color: #fff;
  font-weight: 600;
  border-radius: 8px;
  border: none;
  font-size: 1rem;
  margin-top: 18px;
  cursor: pointer;
  box-shadow: 0 3px 13px rgba(35,72,135,0.10);
  transition: background 0.2s;
}

input[type="submit"]:hover {
  background: #143059;
}

#predictionResult {
  margin-top: 23px;
  text-align: center;
  font-size: 1.08rem;
  color: #317a39;
  background: #e8f5e9;
  border-radius: 8px;
  padding: 14px;
  font-weight: 600;
  border: 1px solid #b9e4c3;
}
