<div class="form-card">
  <h2>Heart Disease Prediction Web Form</h2>
  <form id="predictionForm">
    <label for="age">Age:</label>
    <input type="number" id="age" name="age" required>

    <label for="sex">Sex (1 = male, 0 = female):</label>
    <input type="number" id="sex" name="sex" min="0" max="1" required>

    <label for="cp">Chest Pain Type (0-3):</label>
    <input type="number" id="cp" name="cp" min="0" max="3" required>

    <label for="trestbps">Resting Blood Pressure:</label>
    <input type="number" id="trestbps" name="trestbps" required>

    <label for="chol">Serum Cholestoral (mg/dl):</label>
    <input type="number" id="chol" name="chol" required>

    <!-- Add more dataset questions as appropriate -->

    <input type="submit" value="Predict">
  </form>
  <div id="predictionResult"></div>
</div>

<script>
document.getElementById('predictionForm').addEventListener('submit', function(e) {
  e.preventDefault();
  // For demo: random "prediction" between 0 and 1
  const randomPrediction = Math.random().toFixed(2);
  document.getElementById('predictionResult').innerHTML =
    "Predicted heart disease risk (for demo): <b>" + randomPrediction + "</b>";
});
</script>
